[Form]
public class ATXRequestOrder extends FormRun
{
    ATXInventDimCtrl_Frm_ProductDim    inventDimCtrl_Frm_ProductDim;

}

void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm   inventDimParmEnabled;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init          :
        if (!inventDimCtrl_Frm_ProductDim)
            {
                inventDimCtrl_Frm_ProductDim  = ATXInventDimCtrl_Frm_ProductDim::newFromForm(element);
                inventDimCtrl_Frm_ProductDim.parmSkipOnHandLookUp(true);
            }
        case InventDimFormDesignUpdate::LinkActive    :
        //Fall through
        case InventDimFormDesignUpdate::Active        :
        //Fall through
        case InventDimFormDesignUpdate::FieldChange   :
        if (ATXRequestOrderLine.ItemId)
            {
                inventDimParmEnabled = InventDimParm::activeDimFlag(
                InventDimGroupSetup::newItemId(ATXRequestOrderLine.ItemId));
            }
            else
            {
                inventDimParmEnabled.clearProductDimensions();
            }

            inventDimCtrl_Frm_ProductDim.parmDimParmEnabled(inventDimParmEnabled);
            InventDim.clearNotSelectedDim(inventDimParmEnabled);
            inventDimCtrl_Frm_ProductDim.formSetControls(true);
            break;

        default :
            throw error(strfmt("@PDS1294",funcname()));
    }
}

/// <summary>
///
/// </summary>
public void init()
{
    super();

    element.updateDesign(InventDimFormDesignUpdate::Init);
    ATXRequestOrderHeader_DS.query().dataSourceTable(tablenum(ATXRequestOrderHeader)).addOrderByField(fieldNum(ATXRequestOrderHeader, RequestOrderId),SortOrder::Descending);

    if (!ATXRequestOrderUsers::find(curUserId()))
    {
        UpdateQtyToSalesLine.visible(false);
        ATXRequestOrderHeader_DS.object(fieldNum(ATXRequestOrderHeader, SkipBOMValidation)).allowEdit(false);
    }
    else
    {
        ATXRequestOrderHeader_DS.object(fieldNum(ATXRequestOrderHeader, SkipBOMValidation)).allowEdit(true);
    }
}

/// <summary>
///
/// </summary>
/// <returns></returns>
public int active()
{
    int ret;

    ret = super();
    if (ATXRequestOrderHeader.RequestOrderStatus == ATXRequestOrderStatus::Loaded)
    {
        ATXRequestOrderHeader_DS.allowEdit(true);
        ATXRequestOrderLine_ds.allowEdit(true);
        InventDim_ds.allowEdit(true);
    }
    else
    {
        ATXRequestOrderHeader_DS.allowEdit(false);
        if (!ATXRequestOrderUsers::find(curUserId()))
        {
            ATXRequestOrderLine_ds.allowEdit(false);
        }
        InventDim_ds.allowEdit(false);
    }

    return ret;
}

/// <summary>
///
/// </summary>
/// <returns></returns>
public int active()
{
    int ret;

    ret = super();
    //element.lock();
    element.updateDesign(InventDimFormDesignUpdate::Active);
    //element.unLock();

    return ret;
}

/// <summary>
///
/// </summary>
public void write()
{
    ATXRequestOrderLine.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

    super();

    if (ATXRequestOrderLine.InventDimId != inventDim.InventDimId)
    {
        inventDim.data(InventDim::find(ATXRequestOrderLine.InventDimId));
        inventDim_ds.setCurrent();
    }
}

/// <summary>
///
/// </summary>
/// <returns></returns>
public boolean validateWrite()
{
    boolean ret;

    ret = super();
    ATXRequestOrderLine.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

    return ret;
}

/// <summary>
///
/// </summary>
public void modified()
{
    InventDim   inventDimNewLine;

    super();

    inventDimNewLine                    = InventDim::find(InventDim::inventDimIdBlank());
    inventDimNewLine.InventSiteId       = ATXRequestOrderHeader.InventSiteId;
    inventDimNewLine.InventLocationId   = ATXRequestOrderHeader.InventLocationId;
    inventDimNewLine.wMSLocationId      = InventLocation::find(ATXrequestOrderHeader.InventLocationId).WMSlocationIdDefaultIssue;
    inventDimNewLine                    = InventDim::findOrCreate(inventDimNewLine);
    ATXRequestOrderLine.InventDimId     = inventDimNewLine.inventDimId;
    inventDim.data(inventDimNewLine);
    ATXRequestOrderHeader.ItemId        = ATXRequestOrderLine.ItemId;
    element.updateDesign(InventDimFormDesignUpdate::FieldChange);
}

/// <summary>
///
/// </summary>
/// <param name = "_formControl"></param>
/// <param name = "_filterStr"></param>
public void lookup(FormControl _formControl, str _filterStr)
{
    ATXLookupTableEngine::lookUpByParameters(_formControl, 'ProductDimensionConfigLookup_AT', 'Name', ['ItemId'], [ATXRequestOrderLine.ItemId], ['Name', 'Description']);
}

/// <summary>
///
/// </summary>
public void clicked()
{
    SalesTable salesTableQry;
    SalesLine salesLineUpd;
    ATXBreakdownOrderLine breakdownOrderLineUpd;
    InventDim inventDimQry;

    super();

    // Look for SalesLine
    if (ATXRequestOrderHeader.RequestOrderStatus == ATXRequestOrderStatus::Authorized)
    {
        salesTableQry = SalesTable::find(ATXRequestOrderHeader.SalesId);
        if (salesTableQry)
        {
            select firstonly salesLineUpd
                where salesLineUpd.SalesId == salesTableQry.SalesId
                && salesLineUpd.ItemId == ATXRequestOrderLine.ItemId
                ;

            select firstonly breakdownOrderLineUpd
                where breakdownOrderLineUpd.InventTransId == salesLineUpd.InventTransId
                && breakdownOrderLineUpd.InventSizeId == ATXRequestOrderLine.inventDim().InventSizeId
                ;

            if (breakdownOrderLineUpd)
            {
                ttsbegin;
                breakdownOrderLineUpd.selectForUpdate(true);
                breakdownOrderLineUpd.QtyOrig = ATXRequestOrderLine.QtyOrig;
                breakdownOrderLineUpd.Qty = ATXRequestOrderLine.RequestQty;
                breakdownOrderLineUpd.update();
                ttscommit;
            }
        }
    }
}
